// GANTI TEKS PLACEHOLDER DI BAWAH INI DENGAN BASE64 STRING LOGO ANDA
const logoBase64String =
  "data:image/png;base64,";

let dataPegawai = []; // *** Diubah dari dataMitra ke dataPegawai ***
// HAPUS: const defaultTempat = "Kota Jakarta Barat"; // Variabel ini dihapus/dipindahkan ke dalam addRow()

// Format tanggal ke bentuk “15 Oktober 2025”
function formatTanggalPendek(dateStr) {
  if (!dateStr) return "-";
  const d = new Date(dateStr + "T00:00:00");
  const bulanMap = [
    "Januari",
    "Februari",
    "Maret",
    "April",
    "Mei",
    "Juni",
    "Juli",
    "Agustus",
    "September",
    "Oktober",
    "November",
    "Desember",
  ];
  if (isNaN(d.getTime())) return dateStr;
  return `${d.getDate()} ${bulanMap[d.getMonth()]} ${d.getFullYear()}`;
}

// Ubah string ke Title Case
function toTitleCase(str) {
  if (!str) return "";
  // Tambahkan pengecualian untuk UPPERCASE (misalnya "SST" atau "M.E") agar tetap kapital
  const excludedWords = ["bps", "sst", "m.e"];
  return str
    .toLowerCase()
    .split(" ")
    .map((w) => {
      if (excludedWords.includes(w.toLowerCase())) return w.toUpperCase(); // Biarkan kata tertentu UPPERCASE
      return w.charAt(0).toUpperCase() + w.slice(1);
    })
    .join(" ");
}

// VARIABEL GLOBAL UNTUK PENULISAN PDF (Pastikan ini didefinisikan secara global)
const lineHeight = 5; // Jarak baris standar
let y = 30; // Posisi Y awal

// ===============================
// 2. FUNGSI PEMBUAT PDF SURAT TUGAS
// ===============================

function generateSuratTugas(doc, data) {
  // Reset Y position for new page/document
  y = 30;
  doc.setFont("times", "normal");

  // ==========================================================
  // PERBAIKAN LOGO: MENDAPATKAN PROPERTI ASLI UNTUK RASIO
  // ==========================================================
  const logoProps = doc.getImageProperties(logoBase64String); // Menggunakan jsPDF untuk mendapatkan properti logo
  // Jika logoBase64String TIDAK VALID atau kosong, logoProps akan undifined.
  // Tambahkan pemeriksaan sederhana untuk mencegah error:
  if (!logoProps) {
    console.error(
      "Gagal mendapatkan properti gambar. Pastikan logoBase64String sudah diisi dengan Base64 yang valid."
    );
    // Lanjutkan eksekusi tanpa logo, atau hentikan jika logo wajib
    // return;
  }

  const originalWidth = logoProps.width; // Lebar Piksel Asli
  const originalHeight = logoProps.height; // Tinggi Piksel Asli // Tentukan batas ukuran di PDF (dalam mm). Sesuaikan nilai ini.

  const maxWidthMM = 65; // Lebar tampilan maksimum yang diinginkan di PDF (mm)
  const maxHeightMM = 65; // Tinggi tampilan maksimum yang diinginkan di PDF (mm) // Hitung rasio skala agar gambar tidak melebihi batas (Max Fit)

  const widthRatio = maxWidthMM / originalWidth;
  const heightRatio = maxHeightMM / originalHeight;
  const scale = Math.min(widthRatio, heightRatio); // Ambil skala terkecil untuk menjaga rasio

  const displayWidthMM = originalWidth * scale;
  const displayHeightMM = originalHeight * scale; // Tambahkan logo ke PDF // doc.addImage(imgData, format, x, y, width, height)

  doc.addImage(
    logoBase64String,
    "PNG",
    15,
    12,
    displayWidthMM,
    displayHeightMM
  );

  // Judul
  doc.setFontSize(13);
  doc.setFont("times", "bold");
  doc.text("SURAT TUGAS", 105, y, { align: "center" });
  y += 6;

  doc.setFontSize(11);
  doc.setFont("times", "normal");
  doc.text(`NOMOR : ${data.nomorSuratTugas}`, 105, y, { align: "center" });
  y += 15;

  const marginLeft = 20;
  const marginBody = 45;
  const maxWidthBody = 138;

  // Menimbang
  doc.setFont("times", "normal");
  doc.text("Menimbang", marginLeft, y);
  doc.text(":", marginBody - 3, y);
  doc.text("a.", marginBody, y);
  const menimbangA = `Bahwa dalam rangka kegiatan ${toTitleCase(
    data.namaKegiatan
  )} di lingkungan BPS Kota Jakarta Barat, maka dipandang perlu melakukan kegiatan tersebut.`;
  const menimbangAText = doc.splitTextToSize(menimbangA, maxWidthBody);
  doc.text(menimbangAText, marginBody + 4, y, {
    maxWidth: maxWidthBody,
    align: "justify",
  });
  y += menimbangAText.length * lineHeight + 5;

  // Mengingat
  doc.setFont("times", "normal");
  doc.text("Mengingat", marginLeft, y);
  doc.text(":", marginBody - 3, y);
  const mengingat = [
    "Undang-undang No.16 Tahun 1997 tentang Statistik;",
    "Peraturan Pemerintah No.51 Tahun 1999 tentang Penyelenggaraan Statistik;",
    "Peraturan Pemerintah No.45 Tahun 2013 tentang Pelaksanaan APBN;",
    "Peraturan Presiden Republik Indonesia No.1 Tahun 2025 tentang Perubahan atas Peraturan Presiden Nomor 86 Tahun 2007 tentang BPS;",
    "Peraturan Kepala Badan Pusat Statistik No.7 Tahun 2008 tentang Organisasi dan Tata Kerja BPS;",
    "Keputusan Kepala BPS No.121 Tahun 2001 tentang Organisasi dan Tata Kerja BPS di Daerah;",
  ];
  for (let i = 0; i < mengingat.length; i++) {
    if (y > 270) {
      doc.addPage();
      y = 20;
    }
    const textLine = doc.splitTextToSize(mengingat[i], maxWidthBody);
    doc.text(`${i + 1}.`, marginBody, y);
    doc.text(textLine, marginBody + 4, y, {
      maxWidth: maxWidthBody,
      align: "justify",
    });
    y += textLine.length * lineHeight;
  }
  y += 12;

  // Memberi tugas
  if (y > 270) {
    doc.addPage();
    y = 20;
  }
  doc.setFontSize(12);
  doc.setFont("times", "bold");
  doc.text("MEMBERI TUGAS", 105, y, { align: "center" });
  y += 10;

  // Kepada
  const labelX = 49;
  const valueX = 65;
  const separatorX = 62;
  const separatorX1 = 45;
  const indentX = 22;
  doc.setFontSize(11);
  doc.setFont("times", "normal");
  doc.text("Kepada", indentX, y);
  doc.text(":", separatorX1, y);

  // NAMA PEGAWAI (menggunakan properti 'nama' dari data)
  doc.text("Nama", labelX, y);
  doc.text(":", separatorX, y);
  doc.setFont("times", "bold");
  doc.text(toTitleCase(data.nama), valueX, y);
  y += 5;

  // NIP PEGAWAI (menggunakan properti 'nip' dari data)
  doc.setFont("times", "normal");
  doc.text("NIP", labelX, y);
  doc.text(":", separatorX, y);
  doc.text(data.nip, valueX, y);
  y += 5;

  // JABATAN PEGAWAI (menggunakan properti 'jabatan' dari data)
  doc.setFont("times", "normal");
  doc.text("Jabatan", labelX, y);
  doc.text(":", separatorX, y);
  const jabatanP = `${toTitleCase(data.jabatan)} BPS Kota Jakarta Barat`;
  const jabatanPText = doc.splitTextToSize(jabatanP, maxWidthBody);
  doc.text(jabatanPText, valueX, y, {
    maxWidth: maxWidthBody,
    align: "justify",
  });
  y += jabatanPText.length * lineHeight;
  y += 5; // Tambahkan spasi

  // Untuk
  doc.text("Untuk", indentX, y);
  // Menggunakan data.kegiatanLapangan
  const kegiatanText = `Melakukan ${toTitleCase(
    data.kegiatanLapangan
  )} di ${toTitleCase(data.tempat)} dalam rangka ${toTitleCase(
    data.namaKegiatan
  )} yang dilaksanakan pada tanggal ${data.tanggalKegiatan} di ${data.tempat}.`; // Ditambah data.tempat
  const kegiatanTextLines = doc.splitTextToSize(kegiatanText, maxWidthBody);
  doc.text(":", separatorX1, y);
  doc.text(kegiatanTextLines, labelX, y, {
    maxWidth: maxWidthBody,
    align: "justify",
  });
  y += kegiatanTextLines.length * lineHeight + 15;

  // Penutup
  let rightCenter = 165; // tengah kolom kanan
  let ttdY = y;
  doc.text(
    `Jakarta, ${formatTanggalPendek(data.tanggalSurat)}`,
    rightCenter,
    ttdY,
    { align: "center" }
  );
  ttdY += 5;
  doc.text("Kepala Badan Pusat Statistik", rightCenter, ttdY, {
    align: "center",
  });
  ttdY += 5;
  doc.text("Kota Jakarta Barat", rightCenter, ttdY, { align: "center" });
  ttdY += 25;
  doc.setFont("times", "bold");

  const nameText = "Muhammad Noval SST, M.E."; // Simpan teks dalam variabel
  doc.text(nameText, rightCenter, ttdY, { align: "center" });

  // --- LOGIKA MENAMBAH GARIS BAWAH ---
  // 1. Hitung lebar teks
  const nameWidth =
    (doc.getStringUnitWidth(nameText) * doc.getFontSize()) /
    doc.internal.scaleFactor;

  // 2. Hitung posisi X awal dan akhir (karena teks di tengah)
  const nameStartX = rightCenter - nameWidth / 2;
  const nameEndX = rightCenter + nameWidth / 2;

  // 3. Tentukan posisi Y garis bawah (sedikit di bawah teks)
  const nameUnderlineY = ttdY + 0.6;

  // 4. Gambar garis
  doc.setLineWidth(0.5); // Ketebalan garis 0.5 mm
  doc.line(nameStartX, nameUnderlineY, nameEndX, nameUnderlineY);
  // ------------------------------------

  ttdY += 5;
  doc.setFont("times", "normal");
  doc.text("NIP. 198007182002121003", rightCenter, ttdY, { align: "center" });
}

// ===============================
// 3. LOGIKA VALIDASI & HELPER
// ===============================

function setupNamaKegiatanToUppercase(row) {
  const namaKegiatanInput = row.querySelector('[name="namaKegiatan[]"]');
  if (!namaKegiatanInput) return;

  namaKegiatanInput.addEventListener("input", function () {
    this.value = this.value.toUpperCase();
  });
}

function setupNomorSuratValidation(row) {
  const nomorSuratInput = row.querySelector('[name="nomorSuratTugas[]"]');
  if (!nomorSuratInput) return;

  nomorSuratInput.addEventListener("input", function () {
    this.value = this.value.replace(/\s/g, "");
  });
}

function setupDateValidation(row) {
  const tglSuratInput = row.querySelector('[name="tanggalSurat[]"]');
  const tglMulaiInput = row.querySelector(".tanggal-kegiatan-mulai");
  const tglSelesaiInput = row.querySelector(".tanggal-kegiatan-selesai");

  if (!tglSuratInput || !tglMulaiInput || !tglSelesaiInput) return;

  const updateTglSelesaiConstraint = (startDate) => {
    if (startDate) {
      tglSelesaiInput.setAttribute("min", startDate);
      if (tglSelesaiInput.value && tglSelesaiInput.value < startDate) {
        tglSelesaiInput.value = startDate;
      }
    } else {
      tglSelesaiInput.removeAttribute("min");
    }
  };

  const updateTglMulaiConstraint = (suratDate) => {
    if (suratDate) {
      tglMulaiInput.setAttribute("min", suratDate);
      if (tglMulaiInput.value && tglMulaiInput.value < suratDate) {
        tglMulaiInput.value = suratDate;
      }
      updateTglSelesaiConstraint(tglMulaiInput.value || suratDate);
    } else {
      tglMulaiInput.removeAttribute("min");
      updateTglSelesaiConstraint(tglMulaiInput.value);
    }
  };

  tglSuratInput.addEventListener("change", function () {
    const selectedDate = this.value;
    if (selectedDate) {
      const d = new Date(selectedDate + "T00:00:00");
      const hari = d.getDay();

      if (hari === 0 || hari === 6) {
        alert(
          "Tanggal Surat tidak boleh pada hari Sabtu atau Minggu (Hari libur kerja). Mohon pilih hari kerja."
        );
        this.value = "";
        updateTglMulaiConstraint("");
        return;
      }
    }
    updateTglMulaiConstraint(this.value);
  });

  tglMulaiInput.addEventListener("change", function () {
    updateTglSelesaiConstraint(this.value);
  });
}

// FUNGSI BARU: Mengisi elemen <select> dengan data pegawai
function populateSingleSelect(selectElement) {
  if (!selectElement || dataPegawai.length === 0) return;

  // Hapus opsi lama, tapi pertahankan opsi default
  selectElement.innerHTML =
    '<option value="" disabled selected>Pilih Pegawai</option>';

  dataPegawai.forEach((pegawai) => {
    const option = document.createElement("option");
    // Menggunakan toTitleCase agar cocok dengan hasil pencarian data
    option.value = pegawai.nama;
    option.textContent = pegawai.nama; // Tampilkan nama di dropdown
    selectElement.appendChild(option);
  });
}

