// Tambah baris input
function addRow() {
  const tbody = document.getElementById("pegawai-list-body");

  // LOGIKA BARU: Cek apakah ini baris pertama
  const isFirstRow = tbody.children.length === 0;

  // Tentukan nilai default untuk Kegiatan Lapangan dan Tempat
  const defaultKegiatan = isFirstRow ? "Pemeriksaan Lapangan" : "";
  const defaultTempat = isFirstRow ? "Kota Jakarta Barat" : ""; // Nilai default Tempat hanya di baris pertama

  const newRow = document.createElement("tr");

  newRow.innerHTML = `
    <td><input type="text" name="nomorSuratTugas[]"></td>
    <td><input type="date" name="tanggalSurat[]"></td>
    <td><input type="text" name="namaKegiatan[]"></td>

    <td>
      <div class="date-input-group">
        <label class="date-label">Mulai:</label> <input type="date" name="tanggalKegiatanMulai[]" class="tanggal-kegiatan-mulai">
        <br>
        <label class="date-label">Selesai:</label> <input type="date" name="tanggalKegiatanSelesai[]" class="tanggal-kegiatan-selesai">
      </div>
    </td>

    <td>
        <select name="namaPegawai[]" class="nama-pegawai-select" required>
            <option value="" disabled selected>Pilih Pegawai</option>
            </select>
    </td>

    <td><input type="text" name="kegiatanLapangan[]" value="${defaultKegiatan}"></td> 
    <td><input type="text" name="tempat[]" value="${defaultTempat}"></td>
    <td>
      <div class="button-group">
        <button type="button" class="export-btn">Ekspor</button>
        <button type="button" class="delete-row-btn reset-button">Reset</button>
      </div>
    </td>`;

  tbody.appendChild(newRow);

  // Isi dropdown pada baris yang baru ditambahkan
  const newSelect = newRow.querySelector(".nama-pegawai-select");
  populateSingleSelect(newSelect);

  setupNamaKegiatanToUppercase(newRow);
  setupNomorSuratValidation(newRow);
  setupDateValidation(newRow);
}

// Ambil data dari baris tabel (termasuk format rentang tanggal dan data pegawai)
function getDataFromRow(row) {
  const tglMulai = row.querySelector('[name="tanggalKegiatanMulai[]"]').value;
  const tglSelesai = row.querySelector(
    '[name="tanggalKegiatanSelesai[]"]'
  ).value;
  let tanggalKegiatan;

  // ... (Logika format tanggal tetap sama)
  if (tglMulai && tglSelesai && tglMulai !== tglSelesai) {
    const dateMulai = new Date(tglMulai + "T00:00:00");
    const tglMulaiFormatted = formatTanggalPendek(tglMulai);
    const tglSelesaiFormatted = formatTanggalPendek(tglSelesai);
    const bulanMulai = tglMulaiFormatted.split(" ")[1];
    const bulanSelesai = tglSelesaiFormatted.split(" ")[1];
    const tahunSelesai = tglSelesaiFormatted.split(" ")[2];
    if (bulanMulai === bulanSelesai) {
      tanggalKegiatan = `${dateMulai.getDate()} s.d ${new Date(
        tglSelesai + "T00:00:00"
      ).getDate()} ${bulanSelesai} ${tahunSelesai}`;
    } else {
      tanggalKegiatan = `${tglMulaiFormatted} s.d ${tglSelesaiFormatted}`;
    }
  } else if (tglMulai) {
    tanggalKegiatan = formatTanggalPendek(tglMulai);
  } else {
    tanggalKegiatan = "";
  }

  // Ambil Nama Pegawai dari SELECT
  const namaPegawaiInputVal = row.querySelector('[name="namaPegawai[]"]').value;

  // Cari data Pegawai lengkap berdasarkan nama yang di-input
  const pegawai = dataPegawai.find((p) => p.nama === namaPegawaiInputVal);

  return {
    nomorSuratTugas: row.querySelector('[name="nomorSuratTugas[]"]').value,
    tanggalSurat: row.querySelector('[name="tanggalSurat[]"]').value,
    namaKegiatan: row.querySelector('[name="namaKegiatan[]"]').value,
    tanggalKegiatan: tanggalKegiatan,
    // Menggunakan key 'nama', 'nip', 'jabatan' sesuai struktur pegawai.json
    nama: namaPegawaiInputVal,
    nip: pegawai ? pegawai.nip : "NIP Tidak Ditemukan",
    jabatan: pegawai ? pegawai.jabatan : "Jabatan Tidak Ditemukan",
    // Menggunakan key 'kegiatanLapangan'
    kegiatanLapangan: row.querySelector('[name="kegiatanLapangan[]"]').value,
    tempat: row.querySelector('[name="tempat[]"]').value, // Ambil nilai input Tempat
    rawTglMulai: tglMulai,
    rawTglSelesai: tglSelesai,
    rawTglSurat: row.querySelector('[name="tanggalSurat[]"]').value,
  };
}

// Validasi data (semua field wajib diisi + validasi baru)
function validateData(data) {
  // Menggunakan key 'nama' (namaPegawai) dan 'kegiatanLapangan'
  const requiredFields = [
    "nomorSuratTugas",
    "tanggalSurat",
    "namaKegiatan",
    "nama",
    "kegiatanLapangan",
    "tempat", // Tambahkan Tempat ke requiredFields
  ];
  for (const field of requiredFields) {
    if (
      !data[field] ||
      data[field].trim() === "" ||
      data[field] === "Pilih Pegawai"
    ) {
      return {
        valid: false,
        message:
          "Mohon lengkapi semua data pada baris ini (termasuk Nama Pegawai, Kegiatan Lapangan, dan Tempat).",
      };
    }
  }

  if (!data.tanggalKegiatan || data.tanggalKegiatan.trim() === "") {
    return {
      valid: false,
      message: "Tanggal Kegiatan (Mulai dan/atau Selesai) wajib diisi.",
    };
  }

  if (data.nama.trim() !== "" && data.nip === "NIP Tidak Ditemukan") {
    return {
      valid: false,
      message:
        "Kesalahan data internal: Detail NIP/Jabatan untuk Pegawai yang dipilih tidak ditemukan. Periksa file pegawai.js.",
    };
  }

  // --- VALIDASI TANGGAL SURAT BUKAN HARI SABTU ATAU MINGGU ---
  if (data.rawTglSurat) {
    const tanggalSuratDate = new Date(data.rawTglSurat + "T00:00:00");
    const hari = tanggalSuratDate.getDay();

    if (hari === 0 || hari === 6) {
      return {
        valid: false,
        message:
          "Tanggal Surat tidak boleh pada hari Sabtu atau Minggu (Hari libur kerja).",
      };
    }
  }

  // --- VALIDASI NOMOR SURAT TIDAK BOLEH ADA SPASI ---
  const nomorSurat = data.nomorSuratTugas ? data.nomorSuratTugas.trim() : "";
  if (nomorSurat.includes(" ")) {
    return {
      valid: false,
      message:
        "Nomor Surat tidak boleh mengandung spasi. Harap bersihkan Nomor Surat.",
    };
  }

  // --- VALIDASI TAHUN SURAT HARUS SAMA DENGAN 4 DIGIT TERAKHIR NOMOR SURAT ---
  if (data.tanggalSurat && nomorSurat.length >= 4) {
    const tahunNomorSurat = nomorSurat.slice(-4);
    if (!/^\d{4}$/.test(tahunNomorSurat)) {
      return {
        valid: false,
        message: "4 digit terakhir Nomor Surat harus berupa angka (tahun).",
      };
    }
    const tanggalSuratDate = new Date(data.tanggalSurat + "T00:00:00");
    const tahunTanggalSurat = tanggalSuratDate.getFullYear().toString();
    if (tahunTanggalSurat !== tahunNomorSurat) {
      return {
        valid: false,
        message: `Tahun pada Tanggal Surat (${tahunTanggalSurat}) harus sama dengan 4 digit terakhir Nomor Surat (${tahunNomorSurat}).`,
      };
    }
  } else if (
    data.tanggalSurat &&
    nomorSurat.length < 4 &&
    nomorSurat.length > 0
  ) {
    return {
      valid: false,
      message:
        "Nomor Surat minimal harus memiliki 4 digit (tahun) untuk validasi.",
    };
  }

  // VALIDASI TANGGAL
  if (
    data.rawTglMulai &&
    data.rawTglSurat &&
    new Date(data.rawTglMulai) < new Date(data.rawTglSurat)
  ) {
    return {
      valid: false,
      message: "Tanggal Kegiatan Mulai tidak boleh mendahului Tanggal Surat.",
    };
  }
  if (
    data.rawTglMulai &&
    data.rawTglSelesai &&
    new Date(data.rawTglMulai) > new Date(data.rawTglSelesai)
  ) {
    return {
      valid: false,
      message:
        "Tanggal Kegiatan Selesai tidak boleh mendahului Tanggal Kegiatan Mulai.",
    };
  }

  return { valid: true, message: "Data valid." };
}

// Ekspor 1 surat tugas
async function exportToPDF(row) {
  const data = getDataFromRow(row);
  const validationResult = validateData(data);

  if (!validationResult.valid) {
    alert(`Gagal Ekspor Surat Tugas:\n\n${validationResult.message}`);
    return;
  }

  const doc = new window.jspdf.jsPDF({ unit: "mm", format: "a4" });
  doc.setFont("times", "normal");
  generateSuratTugas(doc, data);
  doc.output("dataurlnewwindow");
}

