// 5. EVENT LISTENER
// ===============================
document.addEventListener("DOMContentLoaded", async () => {
  // 1. Logika pemuatan data pegawai dan pengisian dropdown
  try {
    // --- PERBAIKAN: Menggunakan FETCH untuk memuat file pegawai.json ---
    const response = await fetch("pegawai.json");

    if (!response.ok) {
      // Jika file tidak ditemukan atau ada masalah server
      throw new Error(
        `Gagal memuat pegawai.json: Status HTTP ${response.status}`
      );
    }

    const data = await response.json(); // Parsing konten sebagai JSON

    // Asumsikan data adalah array murni, sesuai dengan snippet yang Anda berikan
    if (Array.isArray(data)) {
      dataPegawai = data;
    } else {
      // Fallback jika ternyata file JSON dibungkus dalam objek, meskipun Anda bilang itu array murni
      throw new Error(
        "Struktur file pegawai.json tidak valid (bukan array pegawai)."
      );
    }
    // ------------------------------------------------------------------

    // Pastikan dataPegawai telah diisi
    if (dataPegawai.length === 0) {
      throw new Error("Data pegawai kosong.");
    }

    console.log(`Berhasil memuat ${dataPegawai.length} data pegawai.`);

    // INI MENJAMIN 5 BARIS AWAL MUNCUL
    for (let i = 0; i < 5; i++) {
      addRow(); // addRow akan memanggil populateSingleSelect
    }
  } catch (error) {
    console.error("Terjadi kesalahan fatal saat memuat data pegawai:", error);
    // Tampilkan pesan kesalahan yang lebih spesifik
    alert(
      `Gagal memuat data pegawai. Kolom Nama Pegawai akan kosong. Pastikan file pegawai.json ada dan benar. Detail: ${error.message}`
    );

    // Tambah 5 Baris Awal meskipun gagal memuat data
    for (let i = 0; i < 5; i++) {
      addRow();
    }
  }

  // 2. Event Listener Utama
  document.getElementById("add-row-button").addEventListener("click", addRow);

  document
    .getElementById("pegawai-list-body")
    .addEventListener("click", (e) => {
      const row = e.target.closest("tr");

      if (e.target.classList.contains("delete-row-btn")) {
        if (confirm("Yakin ingin mengosongkan isian pada baris ini?")) {
          const inputs = row.querySelectorAll(
            'input[type="text"], input[type="date"]'
          );
          inputs.forEach((input) => {
            input.value = "";
            if (input.type === "date") {
              input.removeAttribute("min");
            }
          });

          // Reset dropdown (select)
          const selectPegawai = row.querySelector(".nama-pegawai-select");
          if (selectPegawai) {
            selectPegawai.selectedIndex = 0;
          }

          const allRows = Array.from(
            document.querySelectorAll("#pegawai-list-body tr")
          );
          const rowIndex = allRows.indexOf(row);

          // Reset isian Kegiatan Lapangan (dulu jabatanMitra)
          const kegiatanLapanganInput = row.querySelector(
            '[name="kegiatanLapangan[]"]'
          );
          if (kegiatanLapanganInput) {
            if (rowIndex === 0) {
              kegiatanLapanganInput.value = "Pemeriksaan Lapangan";
            } else {
              kegiatanLapanganInput.value = "";
            }
          }

          // Reset isian Tempat
          const tempatInput = row.querySelector('[name="tempat[]"]');
          if (tempatInput) {
            // Karena defaultTempat sudah dihapus, kita tentukan langsung nilainya
            if (rowIndex === 0) {
              tempatInput.value = "Kota Jakarta Barat";
            } else {
              tempatInput.value = "";
            }
          }

          setupDateValidation(row);
        }
      }

      if (e.target.classList.contains("export-btn")) {
        exportToPDF(row);
      }
    });

  document.getElementById("reset-all-button").addEventListener("click", () => {
    if (
      confirm(
        "Yakin ingin menghapus semua data input dan membuat 5 baris baru?"
      )
    ) {
      document.getElementById("pegawai-list-body").innerHTML = "";
      for (let i = 0; i < 5; i++) {
        addRow();
      }
    }
  });

  document
    .getElementById("export-pdf-button")
    .addEventListener("click", async () => {
      const rows = document.querySelectorAll("#pegawai-list-body tr");
      if (rows.length === 0) {
        alert("Tidak ada data untuk diekspor.");
        return;
      }

      const dataToExport = [];
      const requiredFields = [
        "nomorSuratTugas",
        "tanggalSurat",
        "namaKegiatan",
        "nama",
        "kegiatanLapangan",
        "tempat", // Tambahkan Tempat
      ];

      for (const row of rows) {
        const data = getDataFromRow(row);
        let isRowEmpty = true;

        row.style.border = "";

        for (const field of requiredFields) {
          if (
            data[field] &&
            data[field].trim() !== "" &&
            data[field] !== "NIP Tidak Ditemukan" &&
            data[field] !== "Jabatan Tidak Ditemukan" &&
            data[field] !== "Pilih Pegawai"
          ) {
            isRowEmpty = false;
            break;
          }
        }

        if (isRowEmpty) {
          continue;
        }

        const validationResult = validateData(data);

        if (!validationResult.valid) {
          console.warn(
            `Baris diabaikan (Tidak Valid/Tidak Lengkap): ${
              data.nama || data.nomorSuratTugas
            }`
          );
          continue;
        }

        dataToExport.push(data);
      }

      if (dataToExport.length === 0) {
        alert("Tidak ada baris yang terisi lengkap dan valid untuk diekspor.");
        return;
      }

      const doc = new window.jspdf.jsPDF({ unit: "mm", format: "a4" });
      doc.setFont("times", "normal");

      let firstPage = true;
      for (const data of dataToExport) {
        if (!firstPage) doc.addPage();
        generateSuratTugas(doc, data);
        firstPage = false;
      }

      doc.output("dataurlnewwindow");
    });

  // Tombol copy header
  document.querySelectorAll('[id^="copy-"]').forEach((button) => {
    button.addEventListener("click", (e) => {
      const targetId = e.target.id;
      const targetName = targetId.replace("copy-", "").replace("-btn", "");

      const rows = document.querySelectorAll("#pegawai-list-body tr");
      if (!rows.length) return;

      const isKegiatanLapangan = targetName === "jabatan-mitra"; // Menggunakan ID lama
      const isTanggalKegiatan = targetName === "tgl-kegiatan";
      const isTanggalSurat = targetName === "tgl-surat";
      const isNamaKegiatan = targetName === "nama-kegiatan";
      const isTempat = targetName === "Tempat"; // Cek apakah target adalah Tempat

      const fieldMulai = isTempat
        ? "tempat"
        : targetName === "no-surat"
        ? "nomorSuratTugas"
        : isTanggalSurat
        ? "tanggalSurat"
        : isNamaKegiatan
        ? "namaKegiatan"
        : isKegiatanLapangan
        ? "kegiatanLapangan"
        : "tanggalKegiatanMulai";

      const firstValueMulai = rows[0].querySelector(
        `[name="${fieldMulai}[]"]`
      ).value;
      let firstValueSelesai = "";

      if (isTanggalKegiatan) {
        firstValueSelesai = rows[0].querySelector(
          `[name="tanggalKegiatanSelesai[]"]`
        ).value;
      }

      if (!firstValueMulai && !firstValueSelesai) {
        alert("Kolom di baris pertama kosong.");
        return;
      }

      rows.forEach((row, index) => {
        if (index > 0) {
          if (row.querySelector(`[name="${fieldMulai}[]"]`)) {
            const targetInput = row.querySelector(`[name="${fieldMulai}[]"]`);

            if (targetName === "no-surat") {
              targetInput.value = firstValueMulai.replace(/\s/g, "");
            } else if (isNamaKegiatan) {
              targetInput.value = firstValueMulai.toUpperCase();
            } else {
              targetInput.value = firstValueMulai;
            }

            if (isTanggalSurat) {
              targetInput.dispatchEvent(new Event("change"));
            }
          }

          if (isTanggalKegiatan) {
            const tglMulaiTarget = row.querySelector(
              `[name="tanggalKegiatanMulai[]"]`
            );
            const tglSelesaiTarget = row.querySelector(
              `[name="tanggalKegiatanSelesai[]"]`
            );

            tglMulaiTarget.value = firstValueMulai;
            tglSelesaiTarget.value = firstValueSelesai;
            tglMulaiTarget.dispatchEvent(new Event("change"));
          }
        }
      });
    });
  });
});



